INSERT INTO `itreports` (`Codigo_RPT`, `Descripcion_RPT`, `SQL_RPT`, `Orientacion_RPT`) VALUES ('kventaslocal', 'Reporte Local de Ventas', 'select b.Prefijo_EMI, b.Codigo_EMI, b.Fecha_EMI, b.Codigo_CTZ, c.Nombre_TER, d.Nombre_AGE, e.Nombre_PLA, b.Voucher_EMI, a.Edad_CTZ, a.Modalidad_CTZ, a.FechaIni_CTZ, a.FechaFin_CTZ, a.Dias_CTZ, g.Nombre_DST, h.Nombre_DST, a.Dolares_CTZ, a.Pesos_CTZ, a.Descuento_CTZ, a.Total_CTZ,\r\n concat(f.ID_USR, \'[\', f.Nombre_USR, \']\')\r\nfrom klcotizaciones a, klemisiones b, czterceros c, klagencias d, klplanes e, itusuarios f, kldestinos g, kldestinos h\r\nwhere a.Codigo_DCD=b.Codigo_DCD and a.Codigo_CTZ=b.Codigo_CTZ and a.Codigo_TER=c.Codigo_TER and a.Codigo_AGE=d.Codigo_AGE\r\n and a.Codigo_PLA=e.Codigo_PLA and f.Codigo_USR=b.Codigo_USR and g.Codigo_DST=a.Procedencia_CTZ and h.Codigo_DST=a.Codigo_DST\r\n and b.Estado_EMI<>\'A\' and b.Fecha_EMI between \'@FECHA_INI 00:00:00\' and \'@FECHA_FIN 23:59:59\'', 'L');
UPDATE `itreports` SET `SQL_RPT`='select b.Prefijo_EMI, b.Codigo_EMI, b.Fecha_EMI, b.Codigo_CTZ, c.Nombre_TER, d.Nombre_AGE, e.Nombre_PLA, b.Voucher_EMI, a.Edad_CTZ, a.Modalidad_CTZ, a.FechaIni_CTZ, a.FechaFin_CTZ, a.Dias_CTZ, g.Nombre_DST, h.Nombre_DST, a.Dolares_CTZ, a.Pesos_CTZ, a.Descuento_CTZ, a.Total_CTZ,\r\n concat(f.ID_USR, \'[\', f.Nombre_USR, \']\')\r\nfrom klcotizaciones a, klemisiones b, czterceros c, klagencias d, klplanes e, itusuarios f, kldestinos g, kldestinos h, klagenciasusuarios i\r\nwhere a.Codigo_DCD=b.Codigo_DCD and a.Codigo_CTZ=b.Codigo_CTZ and a.Codigo_TER=c.Codigo_TER and a.Codigo_AGE=d.Codigo_AGE\r\n and a.Codigo_PLA=e.Codigo_PLA and f.Codigo_USR=b.Codigo_USR and g.Codigo_DST=a.Procedencia_CTZ and h.Codigo_DST=a.Codigo_DST\r\n and b.Estado_EMI<>\'A\' and b.Fecha_EMI between \'@FECHA_INI 00:00:00\' and \'@FECHA_FIN 23:59:59\' and i.Codigo_AGE=d.Codigo_AGE and i.Codigo_USR=\'@USUARIO\'' WHERE  `Codigo_RPT`='kventaslocal' AND `Codigo_DCD`=0;
UPDATE `itreports` SET `SQL_RPT`='select b.Prefijo_EMI,  LPAD(b.Codigo_EMI,10,\'0\'), b.Fecha_EMI, b.Codigo_CTZ, c.Nombre_TER, d.Nombre_AGE, e.Nombre_PLA, b.Voucher_EMI, a.Edad_CTZ, a.Modalidad_CTZ, a.FechaIni_CTZ, a.FechaFin_CTZ, a.Dias_CTZ, g.Nombre_DST, h.Nombre_DST, a.Dolares_CTZ, a.Pesos_CTZ, a.Descuento_CTZ, a.Total_CTZ,\r\n concat(f.ID_USR, \'[\', f.Nombre_USR, \']\')\r\nfrom klcotizaciones a, klemisiones b, czterceros c, klagencias d, klplanes e, itusuarios f, kldestinos g, kldestinos h\r\nwhere a.Codigo_DCD=b.Codigo_DCD and a.Codigo_CTZ=b.Codigo_CTZ and a.Codigo_TER=c.Codigo_TER and a.Codigo_AGE=d.Codigo_AGE\r\n and a.Codigo_PLA=e.Codigo_PLA and f.Codigo_USR=b.Codigo_USR and g.Codigo_DST=a.Procedencia_CTZ and h.Codigo_DST=a.Codigo_DST\r\n and b.Estado_EMI<>\'A\' and b.Fecha_EMI between \'@FECHA_INI 00:00:00\' and \'@FECHA_FIN 23:59:59\'\r\n ORDER BY 3' WHERE  `Codigo_RPT`='kventas' AND `Codigo_DCD`=0;
UPDATE `itreports` SET `SQL_RPT`='select b.Prefijo_EMI,   LPAD(b.Codigo_EMI,10,\'0\'), b.Fecha_EMI, b.Codigo_CTZ, c.Nombre_TER, d.Nombre_AGE, e.Nombre_PLA, b.Voucher_EMI, a.Edad_CTZ, a.Modalidad_CTZ, a.FechaIni_CTZ, a.FechaFin_CTZ, a.Dias_CTZ, g.Nombre_DST, h.Nombre_DST, a.Dolares_CTZ, a.Pesos_CTZ, a.Descuento_CTZ, a.Total_CTZ,\r\n concat(f.ID_USR, \'[\', f.Nombre_USR, \']\')\r\nfrom klcotizaciones a, klemisiones b, czterceros c, klagencias d, klplanes e, itusuarios f, kldestinos g, kldestinos h, klagenciasusuarios i\r\nwhere a.Codigo_DCD=b.Codigo_DCD and a.Codigo_CTZ=b.Codigo_CTZ and a.Codigo_TER=c.Codigo_TER and a.Codigo_AGE=d.Codigo_AGE\r\n and a.Codigo_PLA=e.Codigo_PLA and f.Codigo_USR=b.Codigo_USR and g.Codigo_DST=a.Procedencia_CTZ and h.Codigo_DST=a.Codigo_DST\r\n and b.Estado_EMI<>\'A\' and b.Fecha_EMI between \'@FECHA_INI 00:00:00\' and \'@FECHA_FIN 23:59:59\' and i.Codigo_AGE=d.Codigo_AGE and i.Codigo_USR=\'@USUARIO\'\r\nOrder by 6, 3' WHERE  `Codigo_RPT`='kventaslocal' AND `Codigo_DCD`=0;
