UPDATE `itreports` SET `SQL_RPT`='select a.Codigo_ADM, f.Sigla_TID, c.ID_TER, b.Apellido1_PAC, b.Apellido2_PAC, b.Nombre1_PAC, b.Nombre2_PAC, \r\ndate(a.Fecha_ADM), g.Codigo_DGN, sum(i.Cantidad_ORD * l.Valor_TAR),  n.Nombre_SDE, a.estado_adm, a.Autorizacion_ADM \r\nFrom gxadmision a, gxpacientes b, czterceros c,  cztipoid f, gxdiagnostico g, gxordenescab h, gxordenesdet i, gxservicios j, itusuariossedes k, gxmanualestarifarios l, gxcontratos m, czsedes n \r\nWhere a.Codigo_TER=b.Codigo_TER and b.Codigo_TER=c.Codigo_TER and h.Codigo_ORD=i.Codigo_ORD and h.Codigo_ADM=a.Codigo_ADM and k.Codigo_USR=a.Codigo_USR and n.Codigo_SDE=k.Codigo_SDE\r\nand j.Codigo_SER=i.Codigo_SER and f.Codigo_TID=c.Codigo_TID and g.Codigo_DGN=a.Codigo_DGN and l.Codigo_DCD=a.Codigo_DCD and l.Codigo_TAR=m.Codigo_TAR and m.Codigo_DCD=a.Codigo_DCD and m.Codigo_EPS=a.Codigo_EPS and m.Codigo_PLA=a.Codigo_PLA and l.Codigo_SER=i.Codigo_SER and h.Fecha_ORD <= l.FechaFin_TAR and h.Fecha_ORD >= l.FechaIni_TAR \r\nand a.Fecha_ADM>=\'@FECHA_INICIAL\' and a.Fecha_ADM<=\'@FECHA_FINAL\' and a.Codigo_EPS=\'@ENTIDAD\'\r\nand a.estado_adm<>\'A\' \r\nGroup by f.Sigla_TID, c.ID_TER, b.Apellido1_PAC, b.Apellido2_PAC, b.Nombre1_PAC, b.Nombre2_PAC, date(a.Fecha_ADM), g.Descripcion_DGN,  k.Codigo_SDE, a.estado_adm\r\nOrder by 11, 4, 5, 6' WHERE  `Codigo_RPT`='servicioscargadosfecha' AND `Codigo_DCD`=0;
