UPDATE `itreports` SET `SQL_RPT`='select count(*), month(a.Fecha_ADM) as \'PERIODO DEL REPORTE\', o.Nombre_PLA as \'REGIMEN\', e.Nombre_DEP as \'SECCIONAL\', d.Nombre_MUN as \'MUNICIPIO\',f.Sigla_TID as \'TIPO DE IDENTIFICACION\', c.ID_TER as \'No DE IDENTIFICACION\', b.Apellido1_PAC as \'1. APELLIDO\', b.Apellido2_PAC as \'2. APELLIDO\', b.Nombre1_PAC as \'1. NOMBRE\', b.Nombre2_PAC as \'2. NOMBRE\', \r\nTIMESTAMPDIFF(YEAR, b.FechaNac_PAC, CURDATE()) as \'EDAD ACTUAL\', b.Codigo_SEX as \'SEXO\', c.Direccion_TER as \'DIRECCION\', c.Telefono_TER as \'TELEFONO\', \'\' as \'IPS ATENCION PRIMARIA\',\r\ng.Codigo_DGN as \'DIAGNOSTICO\', j.Nombre_SER as \'SERVICIO SOLICITADO\', \'POS\' as \'SERVICIO POS O NO POS\', date(a.Fecha_ADM) as \'FECHA DE SOLICITUD\', date(a.Fecha_ADM) as \'FECHA DE ORDEN MEDICA\', nxs.Razonsocial_DCD as \'IPS QUE SOLICITA LA ATENCION\', \'\' as \'TUTELA\', nxs.Razonsocial_DCD as \'IPS DONDE SE AUTORIZA LA ATENCION\', \'\' as \'TARIFAS (12 -24 HORAS)\',eps.Contrato_EPS as \'NUMERO DE CONTRATO\', \'SI\' as \'CONTRATO LEGALIZADO( SI -NO)\', h.Autorizacion_ORD as \'AUTO No\'  \r\nFrom gxadmision a, gxpacientes b, czterceros c, czmunicipios d, czdepartamentos e, cztipoid f, gxdiagnostico g, gxordenescab h, gxordenesdet i, gxservicios j, gxpacientestipos m, gxplanes o, itconfig nxs, gxeps eps\r\nWhere a.Codigo_TER=b.Codigo_TER and b.Codigo_TER=c.Codigo_TER and d.Codigo_MUN=b.Codigo_MUN and d.Codigo_DEP=b.Codigo_DEP and h.Codigo_ORD=i.Codigo_ORD and h.Codigo_ADM=a.Codigo_ADM and h.Estado_ORD=\'1\' and m.Codigo_PTT=a.Codigo_PTT and Estado_ADM<>\'A\'  \r\nand j.Codigo_SER=i.Codigo_SER and e.Codigo_DEP=b.Codigo_DEP and f.Codigo_TID=c.Codigo_TID and g.Codigo_DGN=a.Codigo_DGN and o.Codigo_PLA=a.Codigo_PLA and eps.Codigo_EPS=a.Codigo_EPS\r\n \r\n\r\n and a.Fecha_ADM>=\'@FECHA_INICIAL\' and a.Fecha_ADM<=\'@FECHA_FINAL 23:59:59\' and a.Codigo_EPS=\'@ENTIDAD\'\r\n\r\nGroup by month(a.Fecha_ADM) , o.Nombre_PLA , e.Nombre_DEP , d.Nombre_MUN ,f.Sigla_TID , c.ID_TER , b.Apellido1_PAC , b.Apellido2_PAC , b.Nombre1_PAC , b.Nombre2_PAC , \r\nTIMESTAMPDIFF(YEAR, b.FechaNac_PAC, CURDATE()), b.Codigo_SEX , c.Direccion_TER , c.Telefono_TER, g.Codigo_DGN   \r\nOrder by month(a.Fecha_ADM) , o.Nombre_PLA , e.Nombre_DEP , d.Nombre_MUN ,f.Sigla_TID , c.ID_TER , b.Apellido1_PAC , b.Apellido2_PAC , b.Nombre1_PAC , b.Nombre2_PAC , \r\nTIMESTAMPDIFF(YEAR, b.FechaNac_PAC, CURDATE()), b.Codigo_SEX , c.Direccion_TER , c.Telefono_TER, g.Codigo_DGN ' WHERE  `Codigo_RPT`='pacientesatendidosxfechaxentidad' AND `Codigo_DCD`=0;
