UPDATE `itreports` SET `SQL_RPT`='Select e.Sigla_TID, b.ID_TER, b.Nombre_TER, c.Codigo_SEX, c.FechaNac_PAC, d.Fecha_HSV, h.Valor_HSV As \'Tensión Arterial\',\r\nsum(Case When g.Nombre_HSV=\'Frecuencia Cardíaca\' Then d.Valor_HSV End) As \'Frecuencia Cardíaca\',\r\nsum(Case When g.Nombre_HSV=\'Frecuencia Respiratoria\' Then d.Valor_HSV End) As \'Frecuencia Respiratoria\',\r\nsum(Case When g.Nombre_HSV=\'Peso\' Then d.Valor_HSV End) As \'Peso\',\r\nsum(Case When g.Nombre_HSV=\'Talla\' Then d.Valor_HSV End) As \'Talla\',\r\nsum(Case When g.Nombre_HSV=\'Temperatura\' Then d.Valor_HSV End) As \'Temperatura\',\r\nsum(Case When g.Nombre_HSV=\'Índice Masa Corporal\' Then d.Valor_HSV End) As \'IMC\'\r\nFrom hcfolios a  \r\nInner Join czterceros b On b.Codigo_TER=a.Codigo_TER\r\nInner Join gxpacientes c On c.Codigo_TER=b.Codigo_TER\r\nInner Join hcsignosvitales d On d.Codigo_TER=a.Codigo_TER and d.Codigo_HCF=a.Codigo_HCF\r\nInner Join cztipoid e On e.Codigo_TID=b.Codigo_TID\r\nInner Join gxadmision f On f.Codigo_ADM=a.Codigo_ADM\r\nInner Join hcsv2 g On g.Codigo_HSV=d.Codigo_HSV\r\nInner Join hcsignosvitales h On h.Codigo_TER=a.Codigo_TER and h.Codigo_HCF=a.Codigo_HCF\r\nWhere f.Estado_ADM<>\'A\' And h.Codigo_HSV=\'01\' And b.ID_TER Like \'%@PACIENTE%\' And d.Fecha_HSV between \'@FECHA_INICIAL\' and \'@FECHA_FINAL\'\r\ngroup by e.Sigla_TID, b.ID_TER, b.Nombre_TER, c.Codigo_SEX, c.FechaNac_PAC, d.Fecha_HSV, h.Valor_HSV \r\nOrder By 2, 6' WHERE  `Codigo_RPT`='hcsigvitalxfecha' AND `Codigo_DCD`=0;
