CREATE TABLE `hcclasiftriage` (	`Codigo_HCT` INT(11) NOT NULL,	`Nombre_HCT` VARCHAR(10) NULL DEFAULT NULL COLLATE 'latin1_swedish_ci',	`Descripcion_HCT` VARCHAR(50) NULL DEFAULT NULL COLLATE 'latin1_swedish_ci',	`Observaciones_HCT` TEXT(1000) NULL DEFAULT NULL COLLATE 'latin1_swedish_ci',	`Color_HCT` VARCHAR(7) NULL DEFAULT NULL COLLATE 'latin1_swedish_ci',	`Class_HCT` VARCHAR(20) NULL DEFAULT NULL COLLATE 'latin1_swedish_ci',	PRIMARY KEY (`Codigo_HCT`),	INDEX `Codigo_HCT` (`Codigo_HCT`)) COMMENT='Clases de Triage' COLLATE='utf8_general_ci' ENGINE=INNODB;
INSERT INTO `hcclasiftriage` (`Codigo_HCT`, `Nombre_HCT`, `Observaciones_HCT`) VALUES ('1', 'Triage I', 'requiere atención inmediata. La condición clínica del paciente representa un riesgo vital y necesita maniobras de reanimación por su compromiso ventilatorio, respiratorio, hemodinámico o neurológico, perdida de miembro u órgano u otras condiciones que por norma exijan atención inmediata.');
INSERT INTO `hcclasiftriage` (`Codigo_HCT`, `Nombre_HCT`, `Observaciones_HCT`) VALUES ('2', 'Triage II', 'la condición clínica del paciente puede evolucionar hacia un rá-pido deterioro o a su muerte, o incrementar el riesgo para la pérdida de un miembro u órgano, por lo tanto, requiere una atención que no debe superar los treinta (30) minutos. La presencia de un dolor extremo de acuerdo con el sistema de clasificación usado debe ser considerada como un criterio dentro de esta categoría.');
INSERT INTO `hcclasiftriage` (`Codigo_HCT`, `Nombre_HCT`, `Observaciones_HCT`) VALUES ('3', 'Triage III', 'la condición clínica del paciente requiere de medidas diagnósticas y terapéuticas en urgencias. Son aquellos pacientes que necesitan un examen complementario o un tratamiento rápido, dado que se encuentran estables desde el punto de vista fisiológico aunque su situación puede empeorar si no se actúa.');
INSERT INTO `hcclasiftriage` (`Codigo_HCT`, `Nombre_HCT`, `Observaciones_HCT`) VALUES ('4', 'Triage IV', 'el paciente presenta condiciones médicas que no comprometen su estado general, ni representan un riesgo evidente para la vida o pérdida de miembro u órgano. No obstante, existen riesgos de complicación o secuelas de la enfermedad o lesión si no recibe la atención correspondiente.\r\n');
INSERT INTO `hcclasiftriage` (`Codigo_HCT`, `Nombre_HCT`, `Observaciones_HCT`) VALUES ('5', 'Triage V', 'el paciente presenta una condición clínica relacionada con problemas agudos o crónicos sin evidencia de deterioro que comprometa el estado general de paciente y no representa un riesgo evidente para la vida o la funcionalidad de miembro u órgano.\r\n');
