UPDATE `itconfig` SET `Version_DCD`='[VIBRANIUM]19.10.18.001';
UPDATE `itreports` SET `SQL_RPT`='Select b.Codigo_FAC, c.ID_TER, c.Nombre_TER, DATE_FORMAT(b.Fecha_FAC, \'%d/%m/%Y\'), b.ValEntidad_FAC \nFrom czradicacionesdet as a, gxfacturas as b, czterceros as c, gxadmision as d \nWhere  trim(a.Codigo_FAC)=trim(b.Codigo_FAC) and c.Codigo_TER=d.Codigo_TER and d.Codigo_ADM=b.Codigo_ADM and \nLPAD(a.Codigo_RAD,10,\'0\')=LPAD(\'@CODIGO_INICIAL\',10,\'0\') \nUnion\nSelect g.Codigo_FAC, concat(e.FechaIni_FAC, \' - \', e.FechaFin_FAC), e.Servicio_FAC, DATE_FORMAT(g.Fecha_FAC, \'%d/%m/%Y\'), g.ValEntidad_FAC \nFrom czradicacionesdet as w, gxfacturas as g, gxfacturascapita as e \nWhere  trim(w.Codigo_FAC)=trim(g.Codigo_FAC) and g.Codigo_FAC=e.Codigo_FAC and \nLPAD(w.Codigo_RAD,10,\'0\')=LPAD(\'@CODIGO_INICIAL\',10,\'0\') \nOrder By 1' WHERE  `Codigo_RPT`='radicaciones' AND `Codigo_DCD`=0;
RENAME TABLE `gxfacturaconf` TO `gxprestadores`;
INSERT INTO `cztipoid` (`Codigo_TID`, `Nombre_TID`, `Sigla_TID`) VALUES ('11', 'Salvoconducto', 'SC');
CREATE TABLE `nxsturnocall` (	`Codigo_TER` VARCHAR(10) NOT NULL COLLATE 'latin1_swedish_ci',	`Fecha_TRN` DATETIME NOT NULL COMMENT 'Ingreso a Cola',	`Codigo_CNS` CHAR(6) NULL DEFAULT NULL COMMENT 'Consultorio',	`Monitor_TRN` INT(11) NOT NULL,	`Estado_TRN` CHAR(1) NULL DEFAULT '1' COMMENT '0: LLAMAR; 1: LLAMADO;' COLLATE 'latin1_swedish_ci',	INDEX `Fecha_TRN` (`Fecha_TRN`),	INDEX `Codigo_TER` (`Codigo_TER`),	INDEX `Estado_TRN` (`Estado_TRN`),	INDEX `Codigo_CNS` (`Codigo_CNS`),	INDEX `Monitor_TRN` (`Monitor_TRN`)) COMMENT='Llamado Turnos Pacientes' COLLATE='utf8_general_ci' ENGINE=InnoDB;
ALTER TABLE `nxsturnocall`	CHANGE COLUMN `Codigo_CNS` `Codigo_CNS` CHAR(6) NOT NULL COMMENT 'Consultorio' AFTER `Fecha_TRN`,	ADD PRIMARY KEY (`Codigo_TER`, `Fecha_TRN`, `Codigo_CNS`);
ALTER TABLE `nxsturnocall` 	ADD COLUMN `Codigo_TRN` INT NOT NULL AUTO_INCREMENT FIRST, 	DROP PRIMARY KEY, 	ADD PRIMARY KEY (`Codigo_TER`, `Fecha_TRN`, `Codigo_CNS`, `Codigo_TRN`), 	ADD UNIQUE INDEX `Codigo_TRN` (`Codigo_TRN`), 	ADD INDEX `Codigo_TRN2` (`Codigo_TRN`);
ALTER TABLE `nxsturnocall`	CHANGE COLUMN `Estado_TRN` `Estado_TRN` CHAR(1) NULL DEFAULT '0' COMMENT '0: LLAMAR; 1: LLAMADO;' COLLATE 'latin1_swedish_ci' AFTER `Monitor_TRN`;
