ALTER TABLE `itconfig_hc` ADD COLUMN `MonitorLaboratorio_XHC` INT NULL DEFAULT '0' COMMENT 'Monitor en el que se visualizará el llamado a Toma de Muestras' AFTER `MonitorCExterna_XHC`;
