UPDATE `itreportsselects` SET `Consulta_RPT`='SELECT DISTINCT \'*\', \'TODOS\'  UNION SELECT DISTINCT b.Codigo_HCT, a.Nombre_HCT  FROM hctipos a, hcfolios b WHERE a.Codigo_HCT=b.Codigo_HCT ORDER BY 1' WHERE  `Codigo_RPT`='hc' AND `Codigo_DCD`=0 AND `Campo_RPT`='FORMATO';
