UPDATE `itreports` SET `SQL_RPT`='SELECT \'\' AS \'No\', MONTH(a.Fecha_HCF) AS \'MES DE ATENCIÓN\', a.Fecha_HCF AS \'FECHA DE ATENCIÓN\', \r\nb.Codigo_DEP AS \'DEPARTAMENTO\', b.Codigo_MUN AS \'MUNICIPIO\', d.Sigla_TID AS \'TIPO DE IDENTIFICACIÓN\', c.ID_TER AS \'NÚMERO DE IDENTIFICACIÓN\',\r\nb.Codigo_PLA AS \'RÉGIMEN\', b.Nombre1_PAC AS \'PRIMER NOMBRE\', b.Nombre2_PAC AS \'SEGUNDO NOMBRE\', b.Apellido1_PAC as \'PRIMER APELLIDO\', b.Apellido2_PAC AS \'SEGUNDO APELLIDO\', 	\r\nb.FechaNac_PAC AS \'FECHA DE NACIMIENTO\', TIMESTAMPDIFF(YEAR,b.FechaNac_PAC,a.Fecha_HCF) AS \'EDAD\', c.Direccion_TER AS \'DIRECCION\', b.Barrio_PAC AS \'BARRIO\',  \r\n\'FECHA DE INGRESO A LA IPS DE GESTIÓN RIESGO\', e.gFUM_HCA AS \'FECHA DE ÚLTIMA MENSTRUACION\', DATE_ADD(e.gFUM_HCA,INTERVAL 281 DAY) AS \'FECHA PROBABLE DE PARTO\',\r\n\'FECHA DE INGRESO AL CONTROL PRENATAL\', \'SEMANA GESTACIÓN AL INGRESO DEL CONTROL PRENATAL\', (ROUND(DATEDIFF(a.Fecha_HCF,e.gFUM_HCA)/30)*3.89) as \'SEMANA DE GESTACIÓN ACTUAL\', f.Pesoprev_HCA AS \'PESO\', f.Talla_HCA AS \'TALLA\', f.IMC_HCA AS \'IMC\', \r\ncase  when f.IMC_HCA < 18.5 then \'BAJO PESO\' when f.IMC_HCA BETWEEN 18.5 AND 24.99 then \'NORMAL\' when f.IMC_HCA BETWEEN 25 AND 29.99 then \'SOBREPESO\' when f.IMC_HCA BETWEEN 30 AND 34.99 then \'OBESIDAD TIPO I\' when f.IMC_HCA BETWEEN 35 AND 39.99 then \'OBESIDAD TIPO II\' ELSE \'OBESIDAD TIPO III\' END as \'CLASIFICACIÓN IMC\', ifnull(g.Numero_HCA,1) AS \'NÚMERO DE CONTROL PRENATAL AL QUE ASISTE A LA FECHA\'\r\n																										\r\n																										\r\n\r\nFROM hcfolios a, gxpacientes b, czterceros c, cztipoid d, hcant_ginecobst e, gxareas i, gxeps j, hcembactual f LEFT JOIN hcctrlprentl g ON f.Codigo_TER=g.Codigo_TER AND g.Codigo_HCF=f.Codigo_HCF\r\nWHERE a.Codigo_TER=b.Codigo_TER AND c.Codigo_TER=b.Codigo_TER AND c.Codigo_TID=d.Codigo_TID AND e.Codigo_TER=a.Codigo_TER\r\nAND e.Codigo_HCF=a.Codigo_HCF AND a.Codigo_TER=f.Codigo_TER AND a.Codigo_HCF=f.Codigo_HCF AND i.Codigo_ARE=a.Codigo_ARE AND j.Codigo_EPS=b.Codigo_EPS\r\nAND j.Codigo_TER=\'@ENTIDAD\' AND i.Codigo_SDE=\'@SEDE\' AND a.Fecha_HCF BETWEEN \'@FECHA_INICIAL\' AND \'@FECHA_FINAL 23:59:59\'' WHERE  `Codigo_RPT`='inf_aro' AND `Codigo_DCD`=0;
