ate(lb11.Fecha_SLB)=a.Fecha_HCF AND alb11.Codigo_SLB=lb11.Codigo_SLB  \r\n*/\r\n/*\r\nAND alb1.Codigo_ser=\'1035\' \r\nAND alb2.Codigo_ser=\'1020\' \r\nAND alb4.Codigo_ser=\'1014\' \r\nAND alb5.Codigo_ser=\'1011\' \r\nAND alb6.Codigo_ser=\'1012\' \r\nAND alb7.Codigo_ser=\'1062\' \r\nAND alb8.Codigo_ser IN (\'971\', \'972\') \r\nAND alb9.Codigo_ser=\'935\'  \r\nAND alb10.Codigo_ser IN(\'1002\', \'1003\')\r\nAND alb11.Codigo_ser=\'1016\' */\r\n/* AND alb12.Codigo_ser=\'1016\' */\r\n \r\nAND alb1.Codigo_ser IN (\'1035\', \'1020\', \'1014\', \'1011\', \'1012\',\'1062\', \'971\', \'972\', \'935\', \'1002\',\'1003\',\'1016\') \r\n\r\n AND j.Codigo_TER=\'@ENTIDAD\' AND i.Codigo_SDE=\'@SEDE\' AND a.Fecha_HCF BETWEEN \'@FECHA_INICIAL\' AND \'@FECHA_FINAL 23:59:59\' \r\n\r\nGROUP BY a.Codigo_TER\r\n;\r\n\r\n' WHERE  `Codigo_RPT`='inf_rcv' AND `Codigo_DCD`=0;
