UPDATE `itreports` SET `SQL_RPT`='SELECT a.Codigo_TER, a.ID_TER, a.Nombre_TER, e.Nombre_MUN, b.Folio_HCF, c.Folio_HCF\r\nFROM czterceros a, hcfolios c, gxpacientes d, czmunicipios e, hcfolios b, gxareas f\r\nWHERE d.Codigo_TER=a.Codigo_TER AND d.Codigo_MUN=e.Codigo_MUN AND d.Codigo_DEP=e.Codigo_DEP\r\nAND a.Codigo_TER=c.Codigo_TER AND b.Codigo_TER=a.Codigo_TER AND f.Codigo_ARE=b.Codigo_ARE AND f.Codigo_ARE=c.Codigo_ARE\r\nAND b.Fecha_HCF BETWEEN \'@FECHA_INICIAL\' AND \'@FECHA_FINAL 23:59:59\'\r\nAND c.Fecha_HCF BETWEEN \'@FECHA_INICIAL\' AND \'@FECHA_FINAL 23:59:59\'\r\nAND f.Codigo_SDE=\'@SEDE\' AND d.Codigo_EPS=\'@CONTRATO\' AND d.Codigo_PLA=\'@PLAN\'\r\nGROUP BY a.Codigo_TER, a.ID_TER, a.Nombre_TER, e.Nombre_MUN\r\nHAVING MIN(b.Folio_HCF) and MAX(c.Folio_HCF)\r\nORDER BY @ORDERBY\r\n' WHERE  `Codigo_RPT`='hcxrangos' AND `Codigo_DCD`=0;
