UPDATE `itreports` SET `SQL_RPT`='SELECT concat(c.Codigo_CIT,\'-\',c.Codigo_AGE) AS \'CODIGO CEX\', j.Sigla_TID AS \'TIPO DOC.\', d.ID_TER AS \'IDENTIFICACION\', a.Codigo_SEX AS \'GENERO\', d.Nombre_TER AS \'NOMBRE PACIENTE\', FechaNac_PAC as \'FEC NAC.\', TIMESTAMPDIFF(YEAR,a.FechaNac_PAC,c.FechaGraba_CIT) AS \'EDAD\', \r\n v.Nombre_MUN AS \'MUNICIPIO\', d.Telefono_TER AS \'TELEFONOS\', d.Direccion_TER AS \'DIRECCION\', i.Nombre_EPS AS \'CONTRATO\', g.Nombre_ARE AS \'AREA\', h.Nombre_CNS AS \'CONSULTORIO\', c.Hora_AGE AS \'TURNO\', c.Fecha_AGE AS \'FECHA CITA\', c.FechaDeseada_CIT AS \'FECHA DESEADA\' , c.FechaGraba_CIT AS \'FECHA ASIGNACION\', \r\n  (case c.Estado_CIT when \'X\' then \'CANCELADA\' when \'R\' then \'REPROGRAMADA\' ELSE ( case Atiende_CIT when \'0\' then \'NO ATENDIDO\' ELSE \'ATENDIDO\' END) END) AS \'ESTADO\', gx.Codigo_DGN as \'Cod. Dx.\', gx.Descripcion_DGN AS \'DIAGNOSTICO\', tp.Nombre_HCT AS \'TIPO HC\', \r\n f.Nombre_ESP AS \'ESPECIALIDAD\',e.ID_TER  AS \'ID MEDICO\',e.Nombre_TER  AS \'NOMBRE DEL MEDICO\', case c.TipoConsulta_CIT when \'1\' then \'PRIMERA VEZ\' ELSE \'CONTROL\' END AS \'TIPO CITA\', u.ID_USR AS \'COD. USUARIO\', u.Nombre_USR AS \'NOMBRE USUARIO\'\r\nFrom gxpacientes a, czterceros d, gxareas g, gxconsultorios h, gxeps i, cztipoid j, itusuarios u, czmunicipios v, gxcitasmedicas c \r\nLEFT JOIN gxagendacab b ON c.Codigo_AGE=b.Codigo_AGE\r\nLEFT JOIN czterceros e ON e.Codigo_TER=b.Codigo_TER\r\nLEFT JOIN gxespecialidades f ON f.Codigo_ESP=b.Codigo_ESP\r\nLEFT JOIN gxmedicos med ON med.Codigo_TER=e.Codigo_TER \r\nLEFT JOIN gxadmision ad ON ad.Codigo_TER=c.Codigo_TER AND c.Fecha_AGE=ad.Fecha_ADM \r\nLEFT JOIN hcfolios r ON r.Codigo_TER=c.Codigo_TER  AND r.Fecha_HCF=c.Fecha_AGE AND med.Codigo_USR=r.Codigo_USR\r\nLEFT JOIN hcdiagnosticos dx ON dx.Codigo_TER=r.Codigo_TER -- AND r.Codigo_HCF =dx.Codigo_HCF\r\nLEFT JOIN gxdiagnostico gx ON gx.Codigo_DGN=dx.Codigo_DGN\r\nLEFT JOIN hctipos tp ON tp.Codigo_HCT=r.Codigo_HCT\r\n\r\nWHERE a.Codigo_MUN=v.Codigo_MUN AND a.Codigo_DEP=v.Codigo_DEP AND a.Codigo_TER=d.Codigo_TER AND a.Codigo_TER=c.Codigo_TER and j.Codigo_TID=d.Codigo_TID AND u.Codigo_USR=c.Codigo_USR\r\nand g.Codigo_ARE=b.Codigo_ARE and h.Codigo_CNS=b.Codigo_CNS and i.Codigo_EPS=a.Codigo_EPS  AND Estado_AGE=\'1\' \r\n\r\n\r\n AND @VARFECHA  between \'@FECHA_INICIAL\' and \'@FECHA_FINAL 23:59:59\'  \r\n\r\n GROUP BY c.Codigo_CIT\r\n having MAX(dx.Codigo_HCF)\r\n \r\n Order By  5,2,3,6,1,7' WHERE  `Codigo_RPT`='citasxdia' AND `Codigo_DCD`=0;
