UPDATE `itreports` SET `SQL_RPT`='select a.Razonsocial_DCD, a.NIT_DCD, a.Direccion_DCD, a.Telefonos_DCD, e.Logo2_HCH, b.Nombre_HCT, d.ID_TER, c.Codigo_HCF\r\nFrom\r\n itconfig a, hctipos b, hcfolios c, czterceros d, hcencabezadosdet e\r\nWhere \r\n c.Codigo_TER=d.Codigo_TER and c.Codigo_HCT=b.Codigo_HCT and b.Codigo_HCH=e.Codigo_HCH\r\n  and c.Codigo_ADM>=\'@CODIGO_INICIAL\' and c.Codigo_ADM<=\'@CODIGO_FINAL\'\r\nORDER BY c.Codigo_ADM, c.Codigo_HCF' WHERE  `Codigo_RPT`='hcadmision' AND `Codigo_DCD`=0;
UPDATE `itreports` SET `SQL_RPT`='Select i.Razonsocial_DCD, i.NIT_DCD, h.Codigo_EMI, date(h.Fecha_EMI), h.Codigo_USR, ID_TER as \'Pasaporte\', Nombre_TER as \'Nombres\', Fecha_CTZ as \'Fecha Cotizacion\', Nombre_AGE as \'Agencia\', Nombre_USR as \'Promotor\', nombre_pla as \'Plan\', Modalidad_CTZ as \'Modalidad\', f.nombre_dst as \'Procedencia\', g.nombre_dst as \'Destino\', FechaIni_CTZ as \'Fecha Inicio\', FechaFin_CTZ as \'Fecha Fin\', Dias_CTZ as \'Días\', TRM_CTZ as \'T.R.M.\', Dolares_CTZ as \'Valor en Dolares\', Pesos_CTZ as \'Valor en Pesos\', Voucher_CTZ as \'Voucher\',a.Direccion_TER, a.Telefono_TER, h.Estado_EMI as \'Estado\' \r\nFrom czterceros a, klcotizaciones b, itusuarios c, klagencias d, klplanes e, kldestinos f, kldestinos g, klemisiones h, itconfig i \r\n				Where a.codigo_ter=b.codigo_ter and c.codigo_usr=b.codigo_usr and d.codigo_age=b.codigo_age and e.codigo_pla=b.codigo_pla and  \r\n				f.codigo_dst=procedencia_ctz and g.codigo_dst=b.codigo_dst and\r\n				b.codigo_ctz=h.Codigo_CTZ and estado_ctz=\'2\' and h.Codigo_EMI>=\'@EMISION_INICIAL\' and h.Codigo_EMI<=\'@EMISION_FINAL\' and h.Estado_EMI<>\'0\'' WHERE  `Codigo_RPT`='klemisiones' AND `Codigo_DCD`=0;
CREATE TABLE `klpersonas` (	`Codigo_DCD` INT(5) NOT NULL DEFAULT '0',	`Codigo_EMI` VARCHAR(10) NOT NULL DEFAULT '0',	`Codigo_TER` VARCHAR(10) NOT NULL DEFAULT '0',	`Parentesco_PER` VARCHAR(50) NULL DEFAULT NULL,	PRIMARY KEY (`Codigo_EMI`, `Codigo_DCD`,`Codigo_TER`),	INDEX `Codigo_TER` (`Codigo_TER`),	INDEX `Codigo_EMI` (`Codigo_EMI`)) COMMENT='Acompañantes Emisiones' COLLATE='utf8_general_ci' ENGINE=InnoDB;
ALTER TABLE `klclientes`	ADD COLUMN `FechaNac_KLI` DATE NULL DEFAULT NULL AFTER `Apellidos_KLI`;
UPDATE `itreports` SET `SQL_RPT`='Select i.Razonsocial_DCD, i.NIT_DCD, h.Codigo_EMI, date(h.Fecha_EMI), h.Codigo_USR, ID_TER as \'Pasaporte\', Nombre_TER as \'Nombres\', FechaNac_KLI as \'Fecha Nacimiento\', Nombre_AGE as \'Agencia\', Nombre_USR as \'Promotor\', nombre_pla as \'Plan\', Modalidad_CTZ as \'Modalidad\', f.nombre_dst as \'Procedencia\', g.nombre_dst as \'Destino\', FechaIni_CTZ as \'Fecha Inicio\', FechaFin_CTZ as \'Fecha Fin\', Dias_CTZ as \'Días\', TRM_CTZ as \'T.R.M.\', Dolares_CTZ as \'Valor en Dolares\', Pesos_CTZ as \'Valor en Pesos\', Voucher_CTZ as \'Voucher\',a.Direccion_TER, a.Telefono_TER, h.Estado_EMI as \'Estado\' \r\nFrom czterceros a, klcotizaciones b, itusuarios c, klagencias d, klplanes e, kldestinos f, kldestinos g, klemisiones h, itconfig i \r\n				Where a.codigo_ter=b.codigo_ter and c.codigo_usr=b.codigo_usr and d.codigo_age=b.codigo_age and e.codigo_pla=b.codigo_pla and  \r\n				f.codigo_dst=procedencia_ctz and g.codigo_dst=b.codigo_dst and\r\n				b.codigo_ctz=h.Codigo_CTZ and estado_ctz=\'2\' and h.Codigo_EMI>=\'@EMISION_INICIAL\' and h.Codigo_EMI<=\'@EMISION_FINAL\' and h.Estado_EMI<>\'0\'' WHERE  `Codigo_RPT`='klemisiones' AND `Codigo_DCD`=0;
