UPDATE `itreports` SET `SQL_RPT`='Select i.Razonsocial_DCD, i.NIT_DCD, h.Codigo_EMI, date(h.Fecha_EMI), h.Codigo_USR, ID_TER as \'Pasaporte\', Nombre_TER as \'Nombres\', FechaNac_KLI as \'Fecha Nacimiento\', Nombre_AGE as \'Agencia\', Nombre_USR as \'Promotor\', nombre_pla as \'Plan\', Modalidad_CTZ as \'Modalidad\', f.nombre_dst as \'Procedencia\', g.nombre_dst as \'Destino\', FechaIni_CTZ as \'Fecha Inicio\', FechaFin_CTZ as \'Fecha Fin\', Dias_CTZ as \'Días\', TRM_CTZ as \'T.R.M.\', Dolares_CTZ as \'Valor en Dolares\', Pesos_CTZ as \'Valor en Pesos\', Voucher_CTZ as \'Voucher\',a.Direccion_TER, a.Telefono_TER, h.Estado_EMI as \'Estado\' \r\nFrom czterceros a, klcotizaciones b, itusuarios c, klagencias d, klplanes e, kldestinos f, kldestinos g, klemisiones h, itconfig i, klclientes j  \r\n				Where a.codigo_ter=b.codigo_ter and c.codigo_usr=b.codigo_usr and d.codigo_age=b.codigo_age and e.codigo_pla=b.codigo_pla and j.Codigo_TER=b.Codigo_TER and  \r\n				f.codigo_dst=procedencia_ctz and g.codigo_dst=b.codigo_dst and\r\n				b.codigo_ctz=h.Codigo_CTZ and estado_ctz=\'2\' and h.Codigo_EMI>=\'@EMISION_INICIAL\' and h.Codigo_EMI<=\'@EMISION_FINAL\' and h.Prefijo_EMI<=\'@PREFIJO\' and h.Estado_EMI<>\'0\'' WHERE  `Codigo_RPT`='klemisiones' AND `Codigo_DCD`=0;
UPDATE `itreports` SET `SQL_RPT`='Select i.Razonsocial_DCD, i.NIT_DCD, h.Codigo_EMI, date(h.Fecha_EMI), h.Codigo_USR, ID_TER as \'Pasaporte\', Nombre_TER as \'Nombres\', FechaNac_KLI as \'Fecha Nacimiento\', Nombre_AGE as \'Agencia\', Nombre_USR as \'Promotor\', nombre_pla as \'Plan\', Modalidad_CTZ as \'Modalidad\', f.nombre_dst as \'Procedencia\', g.nombre_dst as \'Destino\', FechaIni_CTZ as \'Fecha Inicio\', FechaFin_CTZ as \'Fecha Fin\', Dias_CTZ as \'Días\', TRM_CTZ as \'T.R.M.\', Dolares_CTZ as \'Valor en Dolares\', Pesos_CTZ as \'Valor en Pesos\', Voucher_CTZ as \'Voucher\',a.Direccion_TER, a.Telefono_TER, h.Estado_EMI as \'Estado\', h.Prefijo_EMI as \'Prefijo\'  \r\nFrom czterceros a, klcotizaciones b, itusuarios c, klagencias d, klplanes e, kldestinos f, kldestinos g, klemisiones h, itconfig i, klclientes j  \r\n				Where a.codigo_ter=b.codigo_ter and c.codigo_usr=b.codigo_usr and d.codigo_age=b.codigo_age and e.codigo_pla=b.codigo_pla and j.Codigo_TER=b.Codigo_TER and  \r\n				f.codigo_dst=procedencia_ctz and g.codigo_dst=b.codigo_dst and\r\n				b.codigo_ctz=h.Codigo_CTZ and estado_ctz=\'2\' and h.Codigo_EMI>=\'@EMISION_INICIAL\' and h.Codigo_EMI<=\'@EMISION_FINAL\' and h.Prefijo_EMI<=\'@PREFIJO\' and h.Estado_EMI<>\'0\'' WHERE  `Codigo_RPT`='klemisiones' AND `Codigo_DCD`=0;
UPDATE `itreports` SET `SQL_RPT`='Select i.Razonsocial_DCD, i.NIT_DCD, h.Codigo_EMI, date(h.Fecha_EMI), h.Codigo_USR, ID_TER as \'Pasaporte\', Nombre_TER as \'Nombres\', FechaNac_KLI as \'Fecha Nacimiento\', Nombre_AGE as \'Agencia\', Nombre_USR as \'Promotor\', nombre_pla as \'Plan\', Modalidad_CTZ as \'Modalidad\', f.nombre_dst as \'Procedencia\', g.nombre_dst as \'Destino\', FechaIni_CTZ as \'Fecha Inicio\', FechaFin_CTZ as \'Fecha Fin\', Dias_CTZ as \'Días\', TRM_CTZ as \'T.R.M.\', Dolares_CTZ as \'Valor en Dolares\', Pesos_CTZ as \'Valor en Pesos\', Voucher_CTZ as \'Voucher\',a.Direccion_TER, a.Telefono_TER, k.Descripcion_EST as \'Estado\', h.Prefijo_EMI as \'Prefijo\'  \r\nFrom czterceros a, klcotizaciones b, itusuarios c, klagencias d, klplanes e, kldestinos f, kldestinos g, klemisiones h, itconfig i, klclientes j, klestadosventas k  \r\nWhere a.codigo_ter=b.codigo_ter and c.codigo_usr=b.codigo_usr and d.codigo_age=b.codigo_age and e.codigo_pla=b.codigo_pla and j.Codigo_TER=b.Codigo_TER and  \r\nf.codigo_dst=procedencia_ctz and g.codigo_dst=b.codigo_dst and k.Codigo_EST=h.Estado_EMI and\r\nb.codigo_ctz=h.Codigo_CTZ and estado_ctz=\'E\' and LPAD(h.Codigo_EMI,10,\'0\')>=LPAD(\'@EMISION_INICIAL\',10,\'0\') and LPAD(h.Codigo_EMI,10,\'0\')<=LPAD(\'@EMISION_FINAL\',10,\'0\')  and h.Prefijo_EMI<=\'@PREFIJO\' ' WHERE  `Codigo_RPT`='klemisiones' AND `Codigo_DCD`=0;
