INSERT INTO `itreports` (`Codigo_RPT`, `Descripcion_RPT`, `SQL_RPT`) VALUES ('factpendxradicar', 'Listado de facturas pendientes por radicar', 'Select\r\n D.Codigo_FAC, DATE_FORMAT(D.Fecha_FAC, \'%d/%m/%Y\'),LPAD(A.Codigo_ADM,10,\'0\'), concat(Q.Sigla_TID,\' \',C.ID_TER), left(C.Nombre_TER, 60), \r\n J.Descripcion_ADM, E.Codigo_EPS, F.Nombre_TER, concat(\'{\',I.Codigo_USR,\'} \', I.ID_USR), D.ValPaciente_FAC, D.ValEntidad_FAC, K.Codigo_RAD,  DATE_FORMAT(L.FechaConf_RAD, \'%d/%m/%Y\'), D.ValCredito_FAC, (D.ValPaciente_FAC+ D.ValEntidad_FAC - D.ValCredito_FAC) \r\nFrom\r\n czterceros AS C, gxeps AS E, czterceros AS F, cztipoid as Q, \r\n itusuarios AS I, gxtipoingreso AS J, gxadmision AS A, gxfacturas AS D,\r\n czradicacionesdet AS K, czradicacionescab AS L \r\nWhere\r\n L.Codigo_RAD=K.Codigo_RAD and D.Codigo_FAC=K.Codigo_FAC and A.Codigo_TER = C.Codigo_TER \r\n AND Q.Codigo_TID = C.Codigo_TID and D.Codigo_ADM=A.Codigo_ADM\r\n AND F.Codigo_TER = E.Codigo_TER AND trim(D.Codigo_EPS) = trim(E.Codigo_EPS) \r\n AND D.Codigo_USR = I.Codigo_USR AND J.Tipo_ADM = A.Ingreso_ADM and D.Estado_FAC=\'1\'\r\n AND L.Estado_RAD=\'2\'\r\n AND L.FechaConf_RAD>=\'@FECHA_INICIAL 00:00:00\' AND L.FechaConf_RAD<=\'@FECHA_FINAL 23:59:59\'\r\nOrder By\r\nL.Codigo_RAD, L.FechaConf_RAD, D.Codigo_FAC');
INSERT INTO `itreportsparam` (`Codigo_RPT`, `Campo_RPT`, `Titulo_RPT`, `Orden_RPT`, `Tipo_RPT`) VALUES ('factpendxradicar', 'FECHA_FINAL', 'Fecha Final', '2', 'D');
INSERT INTO `itreportsparam` (`Codigo_RPT`, `Campo_RPT`, `Titulo_RPT`, `Orden_RPT`, `Tipo_RPT`) VALUES ('factpendxradicar', 'FECHA_INICIAL', 'Fecha Inicial', '1', 'D');
